// –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–ª–Ω–æ—Ç—ã –¥–∞–Ω–Ω—ã—Ö –∑–∞ –ø–æ—Å–ª–µ–¥–Ω–∏–µ 10 –º–µ—Å—è—Ü–µ–≤
// –û–ü–¢–ò–ú–ò–ó–ê–¶–ò–Ø: –†–∞–∑–±–∏–≤–∞–µ–º –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤ –Ω–∞ –≥—Ä—É–ø–ø—ã –ø–æ 5 —Å–∞–π—Ç–æ–≤ (–æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ SimilarWeb)
const competitors = $('–ß–∏—Ç–∞–µ–º –≤—Å–µ—Ö –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤').all();
const results = $('–ß–∏—Ç–∞–µ–º –≤—Å–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã').all();

// –ß–∏—Ç–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â—É—é –æ—á–µ—Ä–µ–¥—å —á—Ç–æ–±—ã –Ω–µ –¥—É–±–ª–∏—Ä–æ–≤–∞—Ç—å –∑–∞–¥–∞—á–∏
let existingQueue = [];
try {
  existingQueue = $('–ß–∏—Ç–∞–µ–º –æ—á–µ—Ä–µ–¥—å (–ø—Ä–æ–≤–µ—Ä–∫–∞)').all();
} catch (e) {
  // –û—á–µ—Ä–µ–¥—å –º–æ–∂–µ—Ç –±—ã—Ç—å –ø—É—Å—Ç–æ–π
}

// –ú–∞–∫—Å–∏–º—É–º —Å–∞–π—Ç–æ–≤ –≤ –æ–¥–Ω–æ–º –∑–∞–ø—Ä–æ—Å–µ SimilarWeb (–∫–ª–∏–µ–Ω—Ç + 4 –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–∞)
const MAX_SITES_PER_REQUEST = 5;

// –§—É–Ω–∫—Ü–∏—è –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏–∏ –¥–æ–º–µ–Ω–∞ (—É–±–∏—Ä–∞–µ–º https://, www., trailing slash)
function normalizeDomain(url) {
  if (!url) return '';
  return url
    .replace(/^https?:\/\//i, '')
    .replace(/^www\./i, '')
    .replace(/\/+$/, '')
    .toLowerCase()
    .trim();
}

// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Å–ø–∏—Å–æ–∫ –ø–æ—Å–ª–µ–¥–Ω–∏—Ö 10 –º–µ—Å—è—Ü–µ–≤ (—Å —É—á—ë—Ç–æ–º –∑–∞–¥–µ—Ä–∂–∫–∏ SimilarWeb –≤ 2 –º–µ—Å—è—Ü–∞)
// SimilarWeb –Ω–µ –ø–æ–∫–∞–∑—ã–≤–∞–µ—Ç –¥–∞–Ω–Ω—ã–µ –∑–∞ –ø–æ—Å–ª–µ–¥–Ω–∏–µ 2 –º–µ—Å—è—Ü–∞
function getLast10Months() {
  const months = [];
  const now = new Date();

  // –ù–∞—á–∏–Ω–∞–µ–º —Å 3-–≥–æ –º–µ—Å—è—Ü–∞ –Ω–∞–∑–∞–¥ (–ø—Ä–æ–ø—É—Å–∫–∞–µ–º —Ç–µ–∫—É—â–∏–π –∏ –ø—Ä–µ–¥—ã–¥—É—â–∏–π)
  for (let i = 3; i <= 12; i++) {
    const d = new Date(now.getFullYear(), now.getMonth() - i, 1);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    months.push(`${year}.${month}`);
  }

  return months;
}

const last10Months = getLast10Months();

// –ì—Ä—É–ø–ø–∏—Ä—É–µ–º –∫–æ–Ω–∫—É—Ä–µ–Ω—Ç–æ–≤ –ø–æ –∫–ª–∏–µ–Ω—Ç–∞–º (—Å –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏–µ–π –¥–æ–º–µ–Ω–æ–≤)
const clientCompetitors = new Map();

competitors.forEach(row => {
  const client = normalizeDomain(row.json.client_site);
  const competitor = normalizeDomain(row.json.competitors_site);

  if (client && competitor) {
    if (!clientCompetitors.has(client)) {
      clientCompetitors.set(client, []);
    }
    if (!clientCompetitors.get(client).includes(competitor)) {
      clientCompetitors.get(client).push(competitor);
    }
  }
});

// –°–æ–±–∏—Ä–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –∫–ª—é—á–∏ –∏–∑ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤
const existingKeys = new Set();
results.forEach(row => {
  if (row.json.key) {
    existingKeys.add(row.json.key);
  }
});

// –°–æ–±–∏—Ä–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –∑–∞–¥–∞—á–∏ –≤ –æ—á–µ—Ä–µ–¥–∏ (pending –∏ processing)
const existingQueueKeys = new Set();
existingQueue.forEach(row => {
  const status = row.json.status;
  if (status === 'pending' || status === 'processing') {
    // –ö–ª—é—á: client_site + period + sites_list
    const key = `${row.json.client_site}_${row.json.period}_${row.json.sites_list}`;
    existingQueueKeys.add(key);
  }
});

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞–∑–±–∏–µ–Ω–∏—è –º–∞—Å—Å–∏–≤–∞ –Ω–∞ —á–∞–Ω–∫–∏
function chunkArray(array, chunkSize) {
  const chunks = [];
  for (let i = 0; i < array.length; i += chunkSize) {
    chunks.push(array.slice(i, i + chunkSize));
  }
  return chunks;
}

// –°–æ–∑–¥–∞—ë–º –∑–∞–¥–∞—á–∏ —Å —Ä–∞–∑–±–∏–µ–Ω–∏–µ–º –Ω–∞ –≥—Ä—É–ø–ø—ã
const queueTasks = [];

clientCompetitors.forEach((competitorsList, clientSite) => {
  const competitorChunks = chunkArray(competitorsList, MAX_SITES_PER_REQUEST - 1);

  competitorChunks.forEach((chunk, chunkIndex) => {
    last10Months.forEach((period, periodIndex) => {
      const sitesInRequest = [clientSite, ...chunk];
      const sitesList = sitesInRequest.join(',');

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ —É–∂–µ —Ç–∞–∫–∞—è –∑–∞–¥–∞—á–∞ –≤ –æ—á–µ—Ä–µ–¥–∏
      const queueKey = `${clientSite}_${period}_${sitesList}`;
      if (existingQueueKeys.has(queueKey)) {
        return; // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º - –∑–∞–¥–∞—á–∞ —É–∂–µ –≤ –æ—á–µ—Ä–µ–¥–∏
      }

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –Ω–µ–¥–æ—Å—Ç–∞—é—â–∏–µ –¥–∞–Ω–Ω—ã–µ –¥–ª—è —ç—Ç–æ–π –≥—Ä—É–ø–ø—ã
      const missingSites = sitesInRequest.filter(site => {
        const key = `${period}_${site}`;
        return !existingKeys.has(key);
      });

      // –ï—Å–ª–∏ –µ—Å—Ç—å —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω —Å–∞–π—Ç –±–µ–∑ –¥–∞–Ω–Ω—ã—Ö - —Å–æ–∑–¥–∞—ë–º –∑–∞–¥–∞—á—É
      if (missingSites.length > 0) {
        const taskId = `${clientSite}_${period}_chunk${chunkIndex}_${Date.now()}`;

        queueTasks.push({
          queue_id: taskId,
          client_site: clientSite,
          sites_list: sitesList,
          chunk_index: chunkIndex,
          total_chunks: competitorChunks.length,
          period: period,
          status: 'pending',
          priority: periodIndex,
          created_at: new Date().toISOString().replace('T', ' ').substring(0, 19),
          task_id: '',
          error_message: ''
        });
      }
    });
  });
});

queueTasks.sort((a, b) => b.priority - a.priority);

const summary = {
  totalClients: clientCompetitors.size,
  totalTasks: queueTasks.length,
  skippedExisting: existingQueueKeys.size,
  periods: last10Months,
  maxSitesPerRequest: MAX_SITES_PER_REQUEST
};

console.log('Queue tasks summary:', summary);

if (queueTasks.length === 0) {
  return [{ json: { noNewTasks: true, summary } }];
}

return queueTasks.map(task => ({ json: task }));

// –ë–µ—Ä—ë–º –¥–æ 9 –∑–∞–¥–∞—á –∏–∑ –æ—á–µ—Ä–µ–¥–∏ –∏ –¥–æ–±–∞–≤–ª—è–µ–º —Ä–∞–Ω–¥–æ–º–Ω—ã–µ –∑–∞–¥–µ—Ä–∂–∫–∏
const items = $input.all();

if (items.length === 0) {
  return [];
}

// –ë–µ—Ä—ë–º –º–∞–∫—Å–∏–º—É–º 9 –∑–∞–¥–∞—á (–ª–∏–º–∏—Ç Browse AI –≤ —á–∞—Å)
const tasksToProcess = items.slice(0, 9);

// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –ø—Å–µ–≤–¥–æ-—Ä–∞–Ω–¥–æ–º–Ω—ã–µ –∑–∞–¥–µ—Ä–∂–∫–∏ (3-7 –º–∏–Ω—É—Ç –º–µ–∂–¥—É –∑–∞–¥–∞—á–∞–º–∏)
const tasks = tasksToProcess.map((item, index) => {
  // –ë–∞–∑–æ–≤–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞ 3-4 –º–∏–Ω—É—Ç—ã + —Ä–∞–Ω–¥–æ–º 0-3 –º–∏–Ω—É—Ç—ã
  const baseDelay = 180 + Math.floor(Math.random() * 60); // 180-240 —Å–µ–∫
  const randomExtra = Math.floor(Math.random() * 180); // 0-180 —Å–µ–∫
  const delaySeconds = baseDelay + randomExtra; // 180-420 —Å–µ–∫ (3-7 –º–∏–Ω)

  // –ù–∞–∫–æ–ø–∏—Ç–µ–ª—å–Ω–∞—è –∑–∞–¥–µ—Ä–∂–∫–∞ –¥–ª—è –∫–∞–∂–¥–æ–π –∑–∞–¥–∞—á–∏
  const cumulativeDelay = index * delaySeconds;

  return {
    json: {
      ...item.json,
      taskIndex: index,
      delaySeconds: cumulativeDelay,
      delayMinutes: Math.round(cumulativeDelay / 60),
      scheduledAt: new Date(Date.now() + cumulativeDelay * 1000).toISOString()
    }
  };
});

console.log(`Processing ${tasks.length} tasks with random delays`);

return tasks;

// –§–æ—Ä–º–∏—Ä—É–µ–º URL –¥–ª—è –∑–∞–ø—É—Å–∫–∞ —Ä–æ–±–æ—Ç–∞ v2
// –°–∞–π—Ç—ã –ø–æ–¥—Å—Ç–∞–≤–ª—è—é—Ç—Å—è –Ω–∞–ø—Ä—è–º—É—é –≤ URL (Browse.ai –Ω–µ –ø–æ–¥—Å—Ç–∞–≤–ª—è–µ—Ç –ø–ª–µ–π—Å—Ö–æ–ª–¥–µ—Ä—ã)
const input = $input.first().json;

const sites = input.sites_list.split(",");

// –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –ª–∏–º–∏—Ç —Å–∞–π—Ç–æ–≤
if (sites.length > 5) {
  console.warn(`WARNING: Too many sites (${sites.length}), truncating to 5`);
  sites.length = 5;
}

// –§–æ—Ä–º–∏—Ä—É–µ–º sites –¥–ª—è URL
const sitesParam = sites.map(s => s.trim()).join(",");

// queue_id –¥–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è
const queueId = input.queue_id;

// URL —Å –ø–æ–¥—Å—Ç–∞–≤–ª–µ–Ω–Ω—ã–º–∏ —Å–∞–π—Ç–∞–º–∏ –∏ queueId –¥–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è
const performanceUrl = `https://pro.similarweb.com/#/digitalsuite/websiteanalysis/overview/website-performance/*/804/12m?webSource=Total&key=${sitesParam}&qid=${queueId}`;
const aiTrafficUrl = `https://pro.similarweb.com/#/digitalsuite/ai-traffic/overview/*/804/12m?webSource=Total&key=${sitesParam}&qid=${queueId}`;

return [{
  json: {
    ...input,
    performanceUrl,
    aiTrafficUrl,
    sites: sitesParam,
    sitesCount: sites.length,
    queueId
  }
}];

// –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—Ç–∞—Ç—É—Å –∑–∞–¥–∞—á–∏ –∏ –Ω–∞–ø—Ä–∞–≤–ª—è–µ–º –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É –∏–ª–∏ –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –æ—à–∏–±–∫–∏
const input = $input.first().json;

const taskStatus = input.body?.task?.status || input.status;
const taskId = input.body?.task?.id || 'unknown';
const finishedAt = input.body?.task?.finishedAt;
const robotId = input.body?.robot?.id || 'unknown';
const originUrl = input.body?.task?.inputParameters?.originUrl || '';

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–∏–ø —Ä–æ–±–æ—Ç–∞
const isPerformance = originUrl.includes('website-performance');
const robotType = isPerformance ? 'Performance' : 'AI Traffic';

// –ò–∑–≤–ª–µ–∫–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –∏–∑ URL
const keyMatch = originUrl.match(/key=([^&]+)/);
let sites = [];
if (keyMatch) {
  sites = decodeURIComponent(keyMatch[1]).split(',');
}

const periodMatch = originUrl.match(/\/804\/(\d{4}\.\d{2})-/);
const period = periodMatch ? periodMatch[1] : 'unknown';

// –ü—Ä–æ–≤–µ—Ä—è–µ–º —É—Å–ø–µ—à–Ω–æ—Å—Ç—å
const isSuccess = (taskStatus === 'successful' || taskStatus === 'completed') && finishedAt;

if (!isSuccess) {
  // –ó–∞–¥–∞—á–∞ –ø—Ä–æ–≤–∞–ª–∏–ª–∞—Å—å - –ª–æ–≥–∏—Ä—É–µ–º –æ—à–∏–±–∫—É
  // –ü–æ–ª—è —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—Ç –∫–æ–ª–æ–Ω–∫–∞–º –≤ —Ç–∞–±–ª–∏—Ü–µ "–õ–æ–≥–∏ –æ—à–∏–±–æ–∫"
  return [{
    json: {
      isError: true,
      timestamp: new Date().toISOString().replace('T', ' ').substring(0, 19),
      task_id: taskId,
      robot_type: robotType,
      period: period,
      sites: sites.join(','),
      status: taskStatus || 'failed',
      error_message: input.body?.task?.failedReason || 'Unknown error',
      url: originUrl,
      // –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏
      taskId,
      taskStatus,
      robotType,
      originUrl
    }
  }];
}

// –ó–∞–¥–∞—á–∞ —É—Å–ø–µ—à–Ω–∞ - –ø–µ—Ä–µ–¥–∞—ë–º –Ω–∞ –æ–±—Ä–∞–±–æ—Ç–∫—É
return [{
  json: {
    isError: false,
    taskId,
    taskStatus,
    robotType,
    period,
    sites,
    originUrl,
    body: input.body
  }
}];

// –ü–∞—Ä—Å–µ—Ä Performance –¥–∞–Ω–Ω—ã—Ö v3.3
// –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–æ –∏–∑–≤–ª–µ—á–µ–Ω–∏–µ –ø–µ—Ä–∏–æ–¥–∞ –∏–∑ —Ä–∞–∑–Ω—ã—Ö –∏—Å—Ç–æ—á–Ω–∏–∫–æ–≤
const input = $input.first().json;

const body = input.body || input;
const rawData = body?.task?.capturedTexts || {};
const taskId = body?.task?.id || input.taskId || 'unknown';
const originUrl = body?.task?.inputParameters?.originUrl || input.originUrl || '';
const inputSites = input.sites || [];

const keyMatch = originUrl.match(/key=([^&]+)/);
let sitesFromUrl = [];
if (keyMatch) {
  sitesFromUrl = decodeURIComponent(keyMatch[1]).split(',');
}
const clientSite = sitesFromUrl[0] || '';

const qidMatch = originUrl.match(/qid=([^&]+)/);
const queueId = qidMatch ? qidMatch[1] : '';

// –ò–∑–≤–ª–µ–∫–∞–µ–º –ø–µ—Ä–∏–æ–¥ –∏–∑ —Ä–∞–∑–Ω—ã—Ö –∏—Å—Ç–æ—á–Ω–∏–∫–æ–≤
let periodFormatted = '';
// 1. –ò–∑ URL —Ñ–æ—Ä–º–∞—Ç–∞ /804/2025.01-2025.02
const periodUrlMatch = originUrl.match(/\/804\/(\d{4}\.\d{2})-\d{4}\.\d{2}/);
if (periodUrlMatch) {
  periodFormatted = periodUrlMatch[1];
}
// 2. –ò–∑ queueId —Ñ–æ—Ä–º–∞—Ç–∞ site_2025.01_chunk
if (!periodFormatted && queueId) {
  const queuePeriodMatch = queueId.match(/_(\d{4}\.\d{2})_/);
  if (queuePeriodMatch) {
    periodFormatted = queuePeriodMatch[1];
  }
}
// 3. –¢–µ–∫—É—â–∏–π –º–µ—Å—è—Ü –∫–∞–∫ fallback
if (!periodFormatted) {
  const now = new Date();
  periodFormatted = `${now.getFullYear()}.${String(now.getMonth() + 1).padStart(2, '0')}`;
}

let data = {};
if (Array.isArray(rawData)) {
  rawData.forEach(item => {
    const key = item.name || item.key;
    const value = item.newText || item.text || item.value;
    if (key && value) data[key] = value;
  });
} else {
  data = rawData;
}

function isLoading(html) {
  if (!html) return true;
  return html.includes('LoadingContainer') || html.includes('linearGradient id="lineGray"');
}

function extractLegendData(html) {
  if (!html) return {};
  const sites = {};
  const patterns = [
    /data-automation-title="true"[^>]*>([^<]+)<\/label>[\s\S]*?data-automation-value="true"[^>]*>([^<]+)<\/label>/g,
    /class="[^"]*lfbxrA[^"]*">([^<]+)<\/label>[\s\S]{0,300}?class="[^"]*gzsEum[^"]*">([^<]+)<\/label>/g,
    /class="[^"]*iUaIjL[^"]*">([^<]+)<\/label>[\s\S]{0,300}?class="[^"]*gzsEum[^"]*">([^<]+)<\/label>/g
  ];
  patterns.forEach(pattern => {
    let match;
    while ((match = pattern.exec(html)) !== null) {
      const site = match[1].trim();
      const value = match[2].trim();
      if (site && value && site.includes('.') && /^[a-zA-Z0-9]/.test(site)) {
        if (!sites[site]) sites[site] = value;
      }
    }
  });
  return sites;
}

function extractEngagementData(html) {
  if (!html || isLoading(html)) return null;

  const result = {};

  // –ò–∑–≤–ª–µ–∫–∞–µ–º –¥–æ–º–µ–Ω—ã –∏–∑ DomainWrapper
  const domainPattern = /DomainWrapper[^>]*>\s*([a-zA-Z0-9][a-zA-Z0-9.-]*\.[a-zA-Z]{2,})\s*<\/span>/g;
  const domains = [];
  let match;
  while ((match = domainPattern.exec(html)) !== null) {
    domains.push(match[1].trim());
  }

  if (domains.length === 0) return null;

  // –î–ª—è –∫–∞–∂–¥–æ–≥–æ –¥–æ–º–µ–Ω–∞ –∏–∑–≤–ª–µ–∫–∞–µ–º –∑–Ω–∞—á–µ–Ω–∏—è
  domains.forEach(domain => {
    // –ù–∞—Ö–æ–¥–∏–º –∫–æ–ª–æ–Ω–∫—É —Å —ç—Ç–∏–º –¥–æ–º–µ–Ω–æ–º
    const columnStart = html.indexOf('DomainWrapper');
    if (columnStart === -1) return;

    // –ò—â–µ–º –≤—Å–µ —è—á–µ–π–∫–∏ –¥–ª—è —ç—Ç–æ–≥–æ –¥–æ–º–µ–Ω–∞ –ø–æ data-automation
    const cellRegex = new RegExp(
      'data-automation="MiniFlexTable-cell ' + domain.replace(/\./g, '\\.') + '"[\\s\\S]*?<span[^>]*class="[^"]*value[^"]*"[^>]*>([\\s\\S]*?)</span>',
      'g'
    );

    const values = [];
    let cellMatch;
    while ((cellMatch = cellRegex.exec(html)) !== null) {
      let val = cellMatch[1]
        .replace(/<svg[\s\S]*?<\/svg>/g, '')
        .replace(/<div[\s\S]*?<\/div>/g, '')
        .replace(/<[^>]+>/g, '')
        .trim();
      if (val) {
        values.push(val);
      }
    }

    if (values.length >= 5) {
      result[domain] = {
        monthlyVisits: values[0] || '',
        uniqueVisitors: values[1] || '',
        visitsPerVisitor: values[2] || '',
        deduplicatedAudience: values[3] || '',
        visitDuration: values[4] || '',
        pagesPerVisit: values[5] || '',
        bounceRate: values[6] || '',
        pageViews: values[7] || ''
      };
    }
  });

  return Object.keys(result).length > 0 ? result : null;
}

const direct = extractLegendData(data['Direct']);
const organicSearch = extractLegendData(data['Organic Search']);
const paidSearch = extractLegendData(data['Paid Search']);
const displayAds = extractLegendData(data['Display Ads'] || data['Display Search']);
const social = extractLegendData(data['Social'] || data['Sosial']);
const email = extractLegendData(data['Email']);

let engagement = null;
if (!isLoading(data['Engagement'])) {
  engagement = extractEngagementData(data['Engagement']);
}
if (!engagement && data['Engagement 2']) {
  engagement = extractEngagementData(data['Engagement 2']);
}

const today = new Date().toISOString().split('T')[0];

const allSites = new Set();
[direct, organicSearch, paidSearch, displayAds, social, email].forEach(channel => {
  Object.keys(channel).forEach(site => allSites.add(site));
});
if (engagement) {
  Object.keys(engagement).forEach(site => allSites.add(site));
