    // Fallback: label –ø–∞—Ä—ã —Å –¥–æ–º–µ–Ω–∞–º–∏
    /<label[^>]*>([a-zA-Z0-9][a-zA-Z0-9.-]+\.[a-zA-Z]{2,})<\/label>[\s\S]{0,200}?<label[^>]*>([\d,]+|N\/A)<\/label>/g
  ];

  patterns.forEach(pattern => {
    let match;
    while ((match = pattern.exec(html)) !== null) {
